import React from "react";
import './App.css';
import cocktails from './data.js';






  const alcohol = [
    { name: "Rum", checked: false },
    { name: "Vodka", checked: false },
    { name: "Gin", checked: false },
    { name: "Alcohol free", checked: false },
  ]
  export const Checkbox = ({ isChecked, label, checkHandler, index }) => {
    return (
      <div>
        <input
          type="checkbox"
          id={`checkbox-${index}`}
          checked={isChecked}
          onChange={checkHandler}
        />
        <label htmlFor={`checkbox-${index}`}>{label}</label>
      </div>
    )
  }

  function RecipeList() {
    const [alcohol, setAlcohol] = useState(allAlcohol)

  const updateCheckStatus = index => {
    setToppings(
      cocktails.map((alcohol, currentIndex) =>
        currentIndex === index
          ? { ...alcohol, checked: !alcohol.checked }
          : alcohol
      )
    )
    return (
    <div> {/* arrays to pull in the cocktail details from data.js where the data is stored */}
        <h1>Recipes</h1>
        {cocktails.map(cocktail =>
          <div key={cocktail.id}>
            <h2>{cocktail.name}</h2>
            <img src={cocktail.image} alt={cocktail.name} />
            <ul>
              {cocktail.ingredients.map(ingredient =>
                <li key={ingredient}>
                  {ingredient}
                </li>
              )}
            
           <p>{cocktail.recipe}</p>
            
                
            </ul>
          </div>
        )}
      </div>
    );
  }
}
