import './index.css';
import RecipeList from './components/listRecipe.jsx';
import AlcoholSection from './components/conditions.jsx';
import CocktailSelector from './components/cocktailselector';
import { Checkbox } from './components/checkbox';

<title>Cocktail maker 3000</title>
function App() {

const [likings, setLikings] = useState("") 
  const [preferences, setPreferences] = useState( { 'Rum': false, 'Vodka': false, 'Gin': false } ) 

  function togglePreference(cocktails) { 

    preferences[cocktails] = !preferences[cocktails]; 


    let newLikings = ""; 
    for ( var cocktails in preferences ) { 

      if ( preferences[cocktails] ) { 

        newLikings += cocktails + " "; 

      } 

    } 

    setLikings(newLikings); 

  } 

  return ( 

    <div> 

      <Checkbox  togglePreference={togglePreference} cocktails="Rum" /> 
      <Checkbox  togglePreference={togglePreference} cocktails="Vodka" /> 
      <Checkbox togglePreference={togglePreference} cocktails="Gin" /> 

      <h1> My analysis of you: You like {likings} </h1> 

    </div> 

  ) 

} 

