import React from "react";
import './App.css';
import cocktails from './data.js';
import { useState } from "react"



function AlcoholSelection() {
  const [drink, setAlcohol] = useState({
    alcohol: [],
    response: [],
  });
  
  const handleChange = (e) => {
    // Destructuring
    const { value, checked } = e.target;
    const { alcohol } = drink;
      
    console.log(`${value} is ${checked}`);
     
    // Case 1 : The user checks the box
    if (checked) {
      setAlcohol({
        drink: [...alcohol, value],
        response: [...alcohol, value],
      });
    }
  
    // Case 2  : The user unchecks the box
    else {
      setAlcohol({
        drink: drink.filter((e) => e !== value),
        response: drink.filter((e) => e !== value),
      });
    }
  };
    
  return (
    <>
            <div >
              <div >
                <div >
                  <input
                   
                    type="checkbox"
                    name="alcohol"
                    value="Rum"
                    id="flexCheckDefault"
                    onChange={handleChange}
                  />
                  <label
                    
                    htmlFor="flexCheckDefault"
                  >
                      Rum
                  </label>
                </div>
                <div >
                  <input
                   
                    type="checkbox"
                    name="alcohol"
                    value="Vodka"
                    id="flexCheckDefault"
                    onChange={handleChange}
                  />
                  <label
                   
                    htmlFor="flexCheckDefault"
                  >
                      Vodka
                  </label>
                </div>
                <div >
                  <input
                    
                    type="checkbox"
                    name="alcohol"
                    value="Gin"
                    id="flexCheckDefault"
                    onChange={handleChange}
                  />
                  <label
                    htmlFor="flexCheckDefault"
                  >
                      Gin
                  </label>
                </div>
                <div >
                  <input
                    
                    type="checkbox"
                    name="alcohol"
                    value="Alcohol free"
                    id="flexCheckDefault"
                    onChange={handleChange}
                  />
                  <label
                    
                    htmlFor="flexCheckDefault"
                  >
                      Alcohol free
                  </label>
                </div>
              </div>
              
              </div>
  
            <div >
              <label htmlFor="exampleFormControlTextarea1">
                You're proficient in the following languages :{" "}
              </label>
              <textarea
                
                name="response"
                value={drink.response}
                placeholder="The checkbox values will be displayed here "
                id="floatingTextarea2"
                style={{ height: "150px" }}
                onChange={handleChange}
              ></textarea>
            </div>
    </>
  );
}
  
export default AlcoholSelection;
