import React from "react";
import './App.css';
import cocktails from './data.js';
import { useState } from "react"



  const allAlcohol = [
    { alcohol: "Rum", checked: false },
    { alcohol: "Vodka", checked: false },
    { alcohol: "Gin", checked: false },
    { alcohol: "Alcohol free", checked: false },
  ]
  export const Checkbox = ({ isChecked, label, checkHandler, index }) => {
    return (
      <div>
        <input
          type="checkbox"
          id={`checkbox-${index}`}
          checked={isChecked}
          onChange={checkHandler}
        />
        <label htmlFor={`checkbox-${index}`}>{label}</label>
      </div>
    )
  }

  function RecipeList() {
    const [alcohol, setAlcohol] = useState(allAlcohol)

  const updateCheckStatus = index => {
    setAlcohol(
      cocktails.map((alcohols, currentIndex) =>
        currentIndex === index
          ? { ...alcohols, checked: !alcohols.checked }
          : alcohols
      )
    )
    return (

      <div className="App">
      {alcohol.map((alcohols, index) => (
        <Checkbox
          key={alcohols.name}
          isChecked={alcohols.checked}
          checkHandler={() => updateCheckStatus(index)}
          label={alcohols.name}
          index={index}
        />
      ))}
      <p>
        <pre>{JSON.stringify(alcohol, null, 2)}</pre>
      </p>
    </div>
    )
      };
    <div> {/* arrays to pull in the cocktail details from data.js where the data is stored */}
        <h1>Recipes</h1>
        {cocktails.map(cocktail =>
          <div key={cocktail.id}>
            <h2>{cocktail.name}</h2>
            <img src={cocktail.image} alt={cocktail.name} />
            <ul>
              {cocktail.ingredients.map(ingredient =>
                <li key={ingredient}>
                  {ingredient}
                </li>
              )}
            
           <p>{cocktail.recipe}</p>
            
                
            </ul>
          </div>
        )}
      </div>
    ;
  }

  export default RecipeList
